CREATE TABLE IF NOT EXISTS `moh_appointment_service` ( `service_id` int(11) NOT NULL AUTO_INCREMENT, `name` VARCHAR(25) default NULL, `description` VARCHAR(250) default NULL, `concept_id` int(11) default NULL, `retired` SMALLINT(1) NOT NULL default '0', `retire_date` DATE default NULL, `retire_reason` VARCHAR(250) default NULL, `retired_by` INT(11) default NULL, `creator` INT(11) NOT NULL, `created_date` DATE NOT NULL,
PRIMARY KEY (`service_id`),
CONSTRAINT `service_concept` FOREIGN KEY (`concept_id`) REFERENCES `concept` (`concept_id`), CONSTRAINT `service_retired_by` FOREIGN KEY (`retired_by`) REFERENCES `users` (`user_id`), CONSTRAINT `service_creator` FOREIGN KEY (`creator`) REFERENCES `users` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
